        public void drawLandScape(ref Player p1, int xoffset, int yoffset, GameTime gameTime)
        {
            //An idea to save some memory would be to do map looping of ground and landscape together. instead of looping 4 times lets loop twice.

            int xPos = 800, yPos = 600;// Intialize x and y positions. They are 635 because that is the size of our screen

            // adding on where the player has moved the map to
            yPos += p1.MapY;
            xPos += p1.MapX;

            for (int r = yPos/32 - 33; r < yPos/32 +1; ++r)
                for (int c = xPos / 32 - 31; c < xPos/32+2; ++c)
                {
                    if (r >= 0 && c >= 0 && r < 80 && c < 80)
                    {
                        viewedMap[r % 35, c % 35].setTile(r, c, tileMap[r, c].getID());
                        viewedMap[r % 35, c % 35].setXoffset(xoffset);
                        viewedMap[r % 35, c % 35].setYoffset(yoffset);
                        viewedMap[r % 35, c % 35].Draw(gameTime);
                    }
                }

            p1.checkTileCollision(viewedMap);

        }

    }
